
public class Exercise1a {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int x = 0; x < 5; x++){
			if (x%2==0){
				for(int y =0 ; y < 5; y++){
					System.out.print("* ");
					
				}
				System.out.println();
			}
			else {
				for(int y =0 ; y < 5; y++){
					System.out.print(" *");
					
				}
				System.out.println();
			}
		}
	}

}
